#!/usr/bin/env node

const fsScanner = require('./index');

const criterias = fsScanner.argvCriterias(process.argv);
const dir = fsScanner.argvDir(process.argv);

fsScanner.scan(dir, criterias, (err, results) => {
    if (err) {
        throw err;
    } else {
        results.length > 0 ? results.map(result => console.log(result)) : console.log(results);
    }
});